import React, { useEffect, useState } from "react";

const UserList = () => {
    const [users, setUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const usersPerPage = 5;


    useEffect(() => {
        fetchUsers(currentPage);
    }, [currentPage]);

    const fetchUsers = async () => {
        try {


            const token = localStorage.getItem("token"); // Get the token from local storage or session storage
            if (!token) {
                setError("Unauthorized: No token found");
                setLoading(false);
                return;
            }

            const response =await fetch("http://127.0.0.1:5000/api/get_users?page=1&per_page=5", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`  // Add JWT token here
                }
            })

            if (response.status === 401) {  // Token expired or invalid
                alert("Session timed out. Please log in again.");
                localStorage.removeItem("token");  // Remove expired token
                window.location.href = "/login";  // Redirect to login
                return;
            }

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const data = await response.json();
            setUsers(data.users);
            setTotalPages(data.total_pages);
            setCurrentPage(data.current_page);



        } catch (error) {
            console.error("Error fetching categories:", error);
        }
    };

    const nextPage = () => {
        if (currentPage < totalPages) setCurrentPage(currentPage + 1);
    };

    const prevPage = () => {
        if (currentPage > 1) setCurrentPage(currentPage - 1);
    };

    return (
        <div className="grid-item">
            <h2>Admin Users</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.firstname}</td>
                            <td>{user.email}</td>
                            <td>


                                <button className="icon-button" onClick={() => toggleCategoryStatus(user.id)}>
                                    <img src={cat.active ? "/icons/disable.png" : "/icons/no_action.png"} alt="Toggle Status" className="icon-img" />
                                </button>

                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="pagination">
                <button className="button-link" disabled={currentPage === 1} onClick={() => fetchUsers(currentPage - 1)}>Prev</button>
                <span> Page {currentPage} of {totalPages} </span>
                <button className="button-link" disabled={currentPage === totalPages} onClick={() => fetchUsers(currentPage + 1)}>Next</button>
            </div>




            <a href="#" className="add-user-link">Add User</a>
        </div>
    );
};

export default UserList;
