import React, { useEffect, useState } from "react";

const RiskProfileList = () => {
    const [riskProfiles, setRiskProfiles] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

    const fetchRiskProfiles = (page) => {
        fetch(`http://127.0.0.1:5000/api/risk_profiles?page=${page}&per_page=5`)
            .then(response => response.json())
            .then(data => {
                setRiskProfiles(data.risk_profiles);
                setTotalPages(data.total_pages);
                setCurrentPage(data.current_page);
            })
            .catch(error => console.error("Error fetching risk profiles:", error));
    };

    useEffect(() => {
        fetchRiskProfiles(currentPage);
    }, [currentPage]);

    return (
        <div className="grid-item span2">
            <h2>Risk Profiles</h2>
            <table>
                <thead>
                    <tr>
                        <th>Profile Name</th>
                        <th>Description</th>
                        <th>Score Threshold</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {riskProfiles.map((profile) => (
                        <tr key={profile.id}>
                            <td>{profile.profile_name}</td>
                            <td>{profile.description}</td>
                            <td>{profile.score_threshold}</td>
                            <td>{profile.status}</td>
                            <td>
                                <button>Edit</button>
                                <button>{profile.status === "Active" ? "Disable" : "Enable"}</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Pagination Controls */}
            <div className="pagination">
                <button 
                    className="button-link" 
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))} 
                    disabled={currentPage === 1}
                >
                    Prev
                </button>
                <span className="pagination-text"> Page {currentPage} of {totalPages} </span>
                <button 
                    className="button-link" 
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))} 
                    disabled={currentPage === totalPages}
                >
                    Next
                </button>
            </div>

            <a href="#" className="add-user-link">Add Risk Profile</a>
        </div>
    );
};

export default RiskProfileList;
