import React, { useState } from "react";
import Modal from "react-modal";

const AddUser = ({ closeModal, refreshUsers }) => {
    const [formData, setFormData] = useState({
        firstname: "",
        lastname: "",
        new_val1: "",
        new_val2: "",
        user_type: "MEMBER"
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (formData.new_pass.length < 6) {
            alert("Password must be at least 6 characters long.");
            return;
    }
        try {
            const token = localStorage.getItem("token");
            const response = await fetch("http://127.0.0.1:5000/api/add_user", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify(formData)
            });

            const data = await response.json();
            if (data.status === "SUCCESS") {
                alert("User added successfully!");
                refreshUsers();  // Refresh user list
                closeModal();    // Close modal
            } else {
                alert(data.message);
            }
        } catch (error) {
            console.error("Error adding user:", error);
            alert("An error occurred. Please try again.");
        }
    };

    return (
        <Modal isOpen={true} onRequestClose={closeModal} className="modal-content" overlayClassName="modal-overlay">
             <h2>Add User</h2>
                <form onSubmit={handleSubmit} autoComplete="off">
                    <label>First Name:</label>
                    <input type="text" name="firstname" value={formData.firstname} onChange={handleChange} required />

                    <label>Last Name:</label>
                    <input type="text" name="lastname" value={formData.lastname} onChange={handleChange} required />

                    <label>Email:</label>
                    <input type="email" name="new_val1" value={formData.new_val1} onChange={handleChange} required autoComplete="off" />

                    <label>Password:</label>
                    <input type="password" name="new_val2" value={formData.new_val2} onChange={handleChange} required autoComplete="off" />

                    <label>User Type:</label>
                    <select name="user_type" value={formData.user_type} onChange={handleChange} className="input-style">
                        <option value="ADMIN">ADMIN</option>
                        <option value="MEMBER">MEMBER</option>
                    </select>

                    <div className="modal-actions">
                        <button type="button" className="btn-cancel" onClick={closeModal}>Cancel</button>
                        <button type="submit" className="btn-save">Save</button>
                    </div>
                </form>
            </Modal>

    );
};

export default AddUser;
