import React, { useState } from "react";

const UserList = () => {
    // Sample Data (Replace with API Call)
    const sampleUsers = [
        { id: 1, firstname: "John Doe", email: "john@example.com", active: true },
        { id: 2, firstname: "Jane Smith", email: "jane@example.com", active: false },
        { id: 3, firstname: "Alice Johnson", email: "alice@example.com", active: true },
        { id: 4, firstname: "Bob Williams", email: "bob@example.com", active: false }
    ];

    const [users, setUsers] = useState(sampleUsers);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 4;
    const totalPages = Math.ceil(sampleUsers.length / itemsPerPage);


    // Pagination Logic
    const indexOfLastUser = currentPage * usersPerPage;
    const indexOfFirstUser = indexOfLastUser - usersPerPage;
    const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);

    const nextPage = () => {
        if (currentPage < Math.ceil(users.length / usersPerPage)) {
            setCurrentPage(currentPage + 1);
        }
    };

    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    // Edit User (Placeholder)
    const editUser = (id) => {
        console.log("Edit user with ID:", id);
    };

    // Toggle User Status
    const toggleUserStatus = (id) => {
        setUsers(users.map(user => 
            user.id === id ? { ...user, active: !user.active } : user
        ));
    };

    return (
        <div className="grid-item">
            <h2>Admin Users</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {currentUsers.map((user, idx) => (
                            <tr key={idx}>
                                <td>{user.firstname}</td>
                                <td>{user.email}</td>
                                <td>
                                    <button className="button-link" onClick={() => editUser(idx)}> 
                                        <img src="/icons/edit.png" alt="Edit" className="icon-img" />
                                    </button>
                                    <button className="button-link" onClick={() => toggleUserStatus(idx)}>
                                    <img 
                                            src="/icons/disable.png"
                                            alt={user.active ? "Disable User" : "Enable User"} 
                                            className="icon-img"
                                        />
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div className="pagination">
                    <button className="button-link" onClick={prevPage} disabled={currentPage === 1}>Prev</button>
                    <span className="pageLegend">  {currentPage} of {totalPages} </span>
                    <button className="button-link" onClick={nextPage} disabled={currentPage === totalPages}>Next</button>
                </div>
                <a href="#" className="add-user-link">Add New User</a>
        </div>

    );
};
export default UserList;
