import React, { useState, useEffect } from "react";
import UserTable from "./../components/UserTable";
import AddUser from "./../components/modals/AddUser";



const User = () => {
  const [users, setUsers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const [isModalOpen, setIsModalOpen] = useState(false);

    // Function to refresh users (You might fetch users from API here)
    const refreshUsers = () => {
        console.log("User list refreshed.");
    };

  useEffect(() => {
          fetchUsers(currentPage);
      }, [currentPage]);

    const fetchUsers = async () => {
        try {

                const token = localStorage.getItem("token"); // Get the token from local storage or session storage
                if (!token) {
                    console.log("TOKEN failed");
                    return;
                }
                const response =await fetch("http://127.0.0.1:5000/api/get_users", {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${token}`  // Add JWT token here
                    }
                })
                if (response.status === 401) {  // Token expired or invalid
                    alert("Session timed out. Please log in again.");
                    localStorage.removeItem("token");  // Remove expired token
                    window.location.href = "/";  // Redirect to login
                    return;
                }

                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }

                const data = await response.json();
                setUsers(data.users);
                //setTotalPages(data.total_pages);
                //setCurrentPage(data.current_page);
            } catch (error) {
                console.error("Error fetching USERS:", error);
            }
        };


  const updateUser = (updatedUser) => {
    setUsers(users.map(user => user.id === updatedUser.id ? updatedUser : user));
  };

  return (
    <div className="app-container">
      <h1>Manage Users</h1>
      <div className="link-container">
        <a href="/addUser" className="menu-link"> Add Users </a>
        <a href="/addUser" className="menu-link"> Suspend Users </a>
        <a href="/addUser" className="menu-link"> Login Archive  </a>

        <button className="btn-add-user" onClick={() => setIsModalOpen(true)}>Add User</button>

        {isModalOpen && <AddUser closeModal={() => setIsModalOpen(false)} refreshUsers={refreshUsers} />}

      </div>

      <UserTable users={users} updateUser={updateUser} />
    </div>
  );
};

export default User;
