import React, { useState, useEffect } from "react";
import Button from "./../components/ui/Button";  // Use relative path
import { Card, CardContent } from "./../components/ui/Card";
import { Autocomplete } from "./../components/ui/Autocomplete";
import { Switch } from "./../components/ui/Switch";



import "./../styles/settings.css";
import "./../components/ui/ui_components.css";  // Use relative path



export default function Settings() {
  const [orgDetails, setOrgDetails] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [menuAccess, setMenuAccess] = useState([]);

  useEffect(() => {
    fetchOrgDetails();
    fetchUsers();
  }, []);


  const fetchOrgDetails = async () => {
    const response = await fetch("http://127.0.0.1:5000/api/get_organization");
    const data = await response.json();
    setOrgDetails(data);
  };

  const fetchUsers = async () => {
    const response = await fetch("http://127.0.0.1:5000/api/user_emails");
    const data = await response.json();
    setUsers(data);
  };

  const handleUserSelect = async (user) => {
    setSelectedUser(user.id);
    const response = await fetch(`http://127.0.0.1:5000/api/manage_user_acl`,{
    method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ user_id: user.id})
      });
    const data = await response.json();
    setMenuAccess(data);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1 style={{ fontSize: "20px", fontWeight: "bold" }}>Settings</h1>

      {/* Organization Details */}
      <Card>
        <CardContent>
          <h2 style={{ fontSize: "18px", fontWeight: "bold" }}>Organization Details</h2>
          {orgDetails && (
            <div>
              <p>Name: {orgDetails.org_name}</p>
              <p>Portal Name: {orgDetails.portal_name}</p>
              <p>Email: {orgDetails.email}</p>
              <button>Edit</button>
              <Switch checked={orgDetails.status} />
            </div>
          )}
        </CardContent>
      </Card>

       {/* Add New Organization */}
       <Card>
        <CardContent>
          <h2 style={{ fontSize: "18px", fontWeight: "bold" }}>Add New Organization</h2>
          <input
            type="text"
            name="name"
            placeholder="Organization Name"
            value={newOrg.name}
            onChange={handleNewOrgChange}
            style={{ padding: "8px", width: "100%", marginBottom: "10px" }}
          />
          <input
            type="email"
            name="email"
            placeholder="Organization Email"
            value={newOrg.email}
            onChange={handleNewOrgChange}
            style={{ padding: "8px", width: "100%", marginBottom: "10px" }}
          />
          <button onClick={handleAddOrganization} style={{ padding: "8px 16px", cursor: "pointer" }}>
            Add Organization
          </button>
        </CardContent>
      </Card>

      {/* User-Menu Mapping */}
      <Card>
        <CardContent>
          <h2 style={{ fontSize: "18px", fontWeight: "bold" }}>User-Menu Access</h2>
          <Autocomplete
            options={users.map((u) => u.email)}
            onChange={handleUserSelect}
            placeholder="Select user by email"
          />

          <ul>
            {menuAccess.map((menu) => (
              <li key={menu.id}>{menu.name}</li>
            ))}
          </ul>
        </CardContent>
      </Card>
    </div>
  );
}
