import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../api.js";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const data = await loginUser(email, password);

    if (data.status=="SUCCESS") {
      if (data.access_token) {
        localStorage.setItem("token", data.access_token);
        navigate("/adminDashboard");

      } 
      else {
        alert("Login Failed . Try later !");
      }
    }  
    else {
      alert("Login Failed . Retry with valid credentials!");
    }

  };

  return (


    <div className="login-container">
    <div className="login-box">
      <h2>Welcome Back</h2>
      <p className="sub-text">Sign in to access your dashboard</p>
      <form onSubmit={handleLogin}>
        <div className="input-group">
          <label>Email</label>
          <input className="login_input" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div className="input-group">
          <label>Password</label>
          <input className="login_input" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <button type="submit" className="login-btn">Login</button>
      </form>

    </div>
  </div>

  );
};

export default Login;
