import { useState, useEffect } from "react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Autocomplete } from "@/components/ui/autocomplete";
import { Switch } from "@/components/ui/switch";
import "./../styles/settings.css";

export default function Settings() {
  const [orgDetails, setOrgDetails] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [menuAccess, setMenuAccess] = useState([]);

  useEffect(() => {
    fetchOrgDetails();
    fetchUsers();
  }, []);

  const fetchOrgDetails = async () => {
    const response = await fetch("/api/get_organization");
    const data = await response.json();
    setOrgDetails(data);
  };

  const fetchUsers = async () => {
    const response = await fetch("/api/user_emails");
    const data = await response.json();
    setUsers(data);
  };

  const handleUserSelect = async (user) => {
    setSelectedUser(user.id);
    const response = await fetch(`/api/manage_user_acl`,{
    method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ user_id: user.id})
      });
    const data = await response.json();
    setMenuAccess(data);
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold">Settings</h1>

      {/* Organization Details */}
      <Card className="mt-4">
        <CardContent>
          <h2 className="text-lg font-semibold">Organization Details</h2>
          {orgDetails && (
            <div className="space-y-2">
              <p>Name: {orgDetails.org_name}</p>
              <p>Name: {orgDetails.portal_name}</p>
              <p>Email: {orgDetails.email}</p>
              <Button>Edit</Button>
              <Switch checked={orgDetails.status} />
            </div>
          )}
        </CardContent>
      </Card>

      {/* User-Menu Mapping */}
      <Card className="mt-4">
        <CardContent>
          <h2 className="text-lg font-semibold">User-Menu Access</h2>
          <Autocomplete
            options={users.map((u) => u.email)}
            onChange={handleUserSelect}
            placeholder="Select user by email"
          />

          <ul className="mt-2">
            {menuAccess.map((menu) => (
              <li key={menu.id}>{menu.name}</li>
            ))}
          </ul>
        </CardContent>
      </Card>
    </div>
  );
}
