import React, { useEffect, useState } from "react";

const RiskProfileList = () => {
    const [riskProfiles, setRiskProfiles] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);



    useEffect(() => {
        fetchRiskProfiles(currentPage);
    }, [currentPage]);

    const fetchRiskProfiles = async () => {
        try {


            const token = localStorage.getItem("token"); // Get the token from local storage or session storage
            if (!token) {
                setError("Unauthorized: No token found");
                setLoading(false);
                return;
            }

            const response =await fetch("http://127.0.0.1:5000/api/risk_profiles?page=1&per_page=5", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`  // Add JWT token here
                }
            })

            if (response.status === 401) {  // Token expired or invalid
                alert("Session timed out. Please log in again.");
                localStorage.removeItem("token");  // Remove expired token
                window.location.href = "/login";  // Redirect to login
                return;
            }


            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const data = await response.json();
            setRiskProfiles(data.risk_profiles);
            setTotalPages(data.total_pages);
            setCurrentPage(data.current_page);



        } catch (error) {
            console.error("Error fetching categories:", error);
        }
    };

    const nextPage = () => {
        if (currentPage < totalPages) setCurrentPage(currentPage + 1);
    };

    const prevPage = () => {
        if (currentPage > 1) setCurrentPage(currentPage - 1);
    };

    return (
        <div className="grid-item span-2">
            <h2>Risk Profiles</h2>
            <table>
                <thead>
                    <tr>
                        <th>Profile Name</th>
                        <th>Description</th>
                        <th>Score Threshold</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {riskProfiles.map((profile) => (
                        <tr key={profile.id}>
                            <td>{profile.profile_name}</td>
                            <td>{profile.description}</td>
                            <td>{profile.score_threshold}</td>
                            <td>{profile.status}</td>
                            <td>
                                <button>Edit</button>
                                <button>{profile.status === "Active" ? "Disable" : "Enable"}</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Pagination Controls */}
            <div className="pagination">
                <button 
                    className="button-link" 
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))} 
                    disabled={currentPage === 1}
                >
                    Prev
                </button>
                <span className="pagination-text"> Page {currentPage} of {totalPages} </span>
                <button 
                    className="button-link" 
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))} 
                    disabled={currentPage === totalPages}
                >
                    Next
                </button>
            </div>

            <a href="#" className="add-user-link">Add Risk Profile</a>
        </div>
    );
};

export default RiskProfileList;
