import React, { useEffect, useState } from "react";

const UserList = () => {
    const [users, setUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const usersPerPage = 5;

    const fetchUsers = (page) => {
        fetch(`http://127.0.0.1:5000/api/users?page=${page}&per_page=${usersPerPage}`)
            .then(response => response.json())
            .then(data => {
                setUsers(data.users);
                setTotalPages(data.total_pages);
                setCurrentPage(data.current_page);
            })
            .catch(error => console.error("Error fetching users:", error));
    };

    useEffect(() => {
        fetchUsers(currentPage);
    }, [currentPage]);

    return (
        <div className="grid-item">
            <h2>Admin Users</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.firstname}</td>
                            <td>{user.email}</td>
                            <td>
                                <button>Edit</button>
                                <button>{user.active ? "Disable" : "Enable"}</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="pagination">
                <button disabled={currentPage === 1} onClick={() => fetchUsers(currentPage - 1)}>Prev</button>
                <span> Page {currentPage} of {totalPages} </span>
                <button disabled={currentPage === totalPages} onClick={() => fetchUsers(currentPage + 1)}>Next</button>
            </div>
            <a href="#" className="add-user-link">Add User</a>
        </div>
    );
};

export default UserList;
