import { useState } from "react";

const CategoryList = () => {
    // Sample data
    const [categories, setCategories] = useState([
        { id: 1, name: "Travel", description: "Travel based theme", active: true },
        { id: 2, name: "Cricket", description: "Theme related to Cricket", active: true },
        { id: 3, name: "Markets", description: "Capital Market Related Theme", active: false }

    ]);


    const fetchCategories = async () => {
        try {
            const response = await fetch(`/api/categories?page=${currentPage}`);
            const data = await response.json();
            setCategories(data.categories);
            setTotalPages(data.totalPages);
        } catch (error) {
            console.error("Error fetching categories:", error);
        }
    };

    const nextPage = () => {
        if (currentPage < totalPages) setCurrentPage(currentPage + 1);
    };

    const prevPage = () => {
        if (currentPage > 1) setCurrentPage(currentPage - 1);
    };

    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;
    const totalPages = Math.ceil(categories.length / itemsPerPage);

    // Pagination logic
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentCategories = categories.slice(indexOfFirstItem, indexOfLastItem);



    // Edit and Toggle Active Status
    const editCategory = (id) => alert(`Editing category ${id}`);
    const toggleCategoryStatus = (id) => {
        setCategories(categories.map(cat => cat.id === id ? { ...cat, active: !cat.active } : cat));
    };

    return (
        <div className="grid-item">
            <h2>Themes</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Status</th>
                        <th>Edit  Disable</th>
                    </tr>
                </thead>
                <tbody>
                    {currentCategories.map((cat) => (
                        <tr key={cat.id}>
                            <td>{cat.name}</td>
                            <td>{cat.description}</td>
                            <td>{cat.active ? "Active" : "Inactive"}</td>
                            <td>
                                <button className="icon-button" onClick={() => editCategory(cat.id)}>
                                    <img src="/icons/edit.png" alt="Edit" className="icon-img" />
                                </button>
                                <button className="icon-button" onClick={() => toggleCategoryStatus(cat.id)}>
                                    <img src={cat.active ? "/icons/disable.png" : "/icons/no_action.png"} alt="Toggle Status" className="icon-img" />
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="pagination">
                <button className="button-link" onClick={prevPage} disabled={currentPage === 1}>Prev</button>
                <span className="page-info"> Page {currentPage} of {totalPages} </span>
                <button className="button-link" onClick={nextPage} disabled={currentPage === totalPages}>Next</button>
            </div>
            <a href="#" className="add-user-link">Add New Theme</a>
        </div>
    );
};

export default CategoryList;
