import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ResultsPage = () => {
    const location = useLocation();
    const navigate = useNavigate();

    const riskProfile = location.state?.riskProfile || "Unknown Profile";
    const tags = location.state?.tags 
        ? location.state.tags.split(",").map(tag => tag.trim()) 
        : [];


    const userId = location.state?.userId;  // Assuming user_id is passed in state

    const handleValidation = async (validationStatus) => {
        try {
            const response = await fetch("http://127.0.0.1:5000/api/user_risk_validate", {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({
                    user_id: userId,
                    validation: validationStatus
                })
            });

            if (!response.ok) {
                throw new Error("Failed to validate risk profile");
            }

            const result = await response.json();
            console.log("Validation Response:", result);

            alert(`Risk profile ${validationStatus} successfully!`);
            navigate("/adminDashboard");  // Redirect after validation
        } catch (error) {
            console.error("Error validating risk profile:", error);
            alert("Failed to process your request. Please try again.");
        }
    };

    return (
        <div className="results-container">
            <h1>Risk Profile Result</h1>
            <h2>{riskProfile}</h2>
            <p>Tags: {tags.length > 0 ? tags.join(", ") : "No tags available"}</p>

            <div className="action-buttons">
                <button className="accept-btn" onClick={() => handleValidation("Accepted")}>
                    Accept
                </button>
                <button className="reject-btn" onClick={() => handleValidation("Rejected")}>
                    Reject
                </button>
            </div>
        </div>
    );
};

export default ResultsPage;
