import React, { useState } from "react";
import { useNavigate,Link } from "react-router-dom";

const SideBar = () => {
    const [menus, setMenus] = useState([]);
    const [userType, setUserType] = useState("");

    useEffect(() => {
        const fetchMenus = async () => {
            const token = localStorage.getItem("token");

            try {
                const response = await fetch("http://127.0.0.1:5000/api/user_menus", {
                    method: "GET",
                    headers: {
                        "Authorization": `Bearer ${token}`,
                        "Content-Type": "application/json"
                    }
                });

                const data = await response.json();

                if (data.status === "SUCCESS") {
                    setMenus(data.menus); // Set the menu list
                    setUserType(data.user_type); // Set the user type (ADMIN / MEMBER)
                } else {
                    console.error("Error fetching menus:", data.message);
                }
            } catch (error) {
                console.error("API error:", error);
            }
        };

        fetchMenus();
    }, []);

    // **Admin menus (full access)**
    const adminMenus = [
        { menu_name: "Dashboard", link: "/dashboard" },
        { menu_name: "Reports", link: "/reports" },
        { menu_name: "Settings", link: "/settings" },
        { menu_name: "Users", link: "/users" }
    ];

    // **Render menus based on user type**
    const displayedMenus = userType === "ADMIN" ? adminMenus : menus;


  return (

            <ul>
                                  <img src="/icons/Pragmatic_Logo.png"  alt="PRAGMATIC DIGITAL" className="logo_class" />
                                 <br /><br /><br />
                                  <li><Link to="/users">Users</Link></li>
                                  <li><Link to="/categories">Categories</Link></li>
                                  <li><Link to="/risk-profiles">Risk Profiles</Link></li>
                                  <li><Link to="/dashboard"> Test Options </Link></li>
                                  <li><Link to="/reports">Reports</Link></li>
                                  <li><Link to="/reports">Settings</Link></li>
                                  <li>
                                    <button className="logout-button" onClick={() => {
                                        localStorage.removeItem("token"); 
                                        window.location.href = "/"; // Redirect to login
                                    }}>
                                        Logout
                                    </button>
                                  </li>


          </ul>

  );
};

export default SideBar;
