import React from "react";
import { useState } from "react";
import "./../styles/styles.css";
import "./../styles/header.css";

import UserMenu from "./UserMenu";


const Header = () => {

  const token = localStorage.getItem("token");
  const name = localStorage.getItem("name");
  const user_type = localStorage.getItem("user_type");
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <header className="bg-white shadow-md p-4 flex justify-between items-center">
      {/* Left: Organization Name & Logo */}
      <div className="flex items-center gap-3">
        <span className="org_header">  <img src="/icons/Logo.png" alt="Org Logo" className="logo_class" />  </span>

        <span className="org_header">My Organization</span>
      </div>


      {/* Right: Settings & User Preferences */}
      <div className="flex items-center justify-between w-full">
        <div className="flex items-center gap-2">
        <button className="top_logo_class">
            <img src="/icons/user.png" alt="User Pref" className="btn-icon-img" />
          </button>
          {name && <span className="text-sm">Welcome, {name}</span>}
          {user_type && <span className="text-sm">  ({user_type})</span>}
        </div>

        <div className="relative">
      <button 
        className="button-group top_logo_class" 
        onClick={() => setIsMenuOpen(!isMenuOpen)}
      >
        <img src="/icons/settings.png" alt="Settings" className="btn-icon-img" />
      </button>

      {isMenuOpen && (

        <div className="absolute top-full right-0 mt-2">
          <div className="bg-white border border-gray-200 rounded-lg shadow-lg">
            <ul>
              <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Settings</li>
              <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Preferences</li>
            </ul>
          </div>
          <div className="absolute top-0 right-0 mt-2 w-48 bg-white border border-gray-200 rounded-lg shadow-lg">
          <ul>
            <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Profile</li>
            <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Logout</li>
          </ul>
        </div>
        </div>
      )}
    </div>
        <div className="relative inline-block">     
      {token && (
        <div className="relative inline-block">
          <button className="top_logo_class" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            <img src="/icons/user.png" alt="User Pref" className="btn-icon-img" />
          </button>       
          {isMenuOpen && (
            <div className="absolute top-full right-0 mt-2 w-48 bg-white border border-gray-200 rounded-lg shadow-lg">
              <ul>
                <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Profile</li>
                <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Logout</li>
              </ul>
            </div>
          )}  

        </div>
      )}
    </div>





      </div>
    </header>
  );
};

export default Header;
