import React, { useState } from "react";
import "./styles.css";

const AdminDashboard = () => {

    const [categories, setCategories] = useState([]);
    const [questions, setQuestions] = useState([]);

    // User State
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    // Category State
    const [categoryName, setCategoryName] = useState("");

    // Question State
    const [questionText, setQuestionText] = useState("");
    const [options, setOptions] = useState(["", "", "", ""]);
    const [weights, setWeights] = useState([0, 0, 0, 0]);

    // Add User
    const addUser = () => {
        setUsers([...users, { email, password }]);
        setEmail("");
        setPassword("");
    };

    // Add Category
    const addCategory = () => {
        setCategories([...categories, { name: categoryName, enabled: true }]);
        setCategoryName("");
    };

    // Toggle Category Status
    const toggleCategory = (index) => {
        const updatedCategories = [...categories];
        updatedCategories[index].enabled = !updatedCategories[index].enabled;
        setCategories(updatedCategories);
    };

    // Add Question
    const addQuestion = () => {
        setQuestions([...questions, { text: questionText, options, weights }]);
        setQuestionText("");
        setOptions(["", "", "", ""]);
        setWeights([0, 0, 0, 0]);
    };


     const [users, setUsers] = useState([
            { firstname: "John Doe", email: "john@example.com", active: true },
            { firstname: "Jane Smith", email: "jane@example.com", active: true },
            { firstname: "Mike Johnson", email: "mike@example.com", active: true },
            { firstname: "Emily Davis", email: "emily@example.com", active: true },
            { firstname: "Chris Brown", email: "chris@example.com", active: true },
            { firstname: "Sarah Wilson", email: "sarah@example.com", active: true }
        ]);
        const [currentPage, setCurrentPage] = useState(1);
        const usersPerPage = 5;

        // Pagination logic
        const indexOfLastUser = currentPage * usersPerPage;
        const indexOfFirstUser = indexOfLastUser - usersPerPage;
        const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);
        const totalPages = Math.ceil(users.length / usersPerPage);

        const nextPage = () => setCurrentPage(currentPage < totalPages ? currentPage + 1 : currentPage);
        const prevPage = () => setCurrentPage(currentPage > 1 ? currentPage - 1 : currentPage);

        const toggleUserStatus = (index) => {
            const updatedUsers = [...users];
            updatedUsers[indexOfFirstUser + index].active = !updatedUsers[indexOfFirstUser + index].active;
            setUsers(updatedUsers);
        };

        const editUser = (index) => {
            const user = users[indexOfFirstUser + index];
            alert(`Edit user: ${user.name}`);
        };

    return (
        <div className="dashboard-container">
            {/* Sidebar Menu */}
            <nav className="sidebar">
                <h2></h2>
                <ul>
                    <li></li>
                    <li>Users</li>
                    <li>Categories</li>
                    <li>Questions</li>
                    <li>Settings</li>
                </ul>
            </nav>

            {/* Main Content */}
            <div className="main-content">
                <h1>Admin Dashboard</h1>

                <div className="grid-container">
                    {/* User Management */}
                    <div className="grid-item">
                    <h2>Admin Users</h2>
                        <table>
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {currentUsers.map((user, idx) => (
                                    <tr key={idx}>
                                        <td>{user.firstname}</td>
                                        <td>{user.email}</td>
                                        <td>
                                            <button className="button-link" onClick={() => editUser(idx)}>Edit</button>
                                            <button className="button-link" onClick={() => toggleUserStatus(idx)}>
                                                {user.active ? "Disable" : "Enable"}
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                        <div className="pagination">
                            <button className="button-link" onClick={prevPage} disabled={currentPage === 1}>Prev</button>
                            <span>  {currentPage} of {totalPages} </span>
                            <button className="button-link" onClick={nextPage} disabled={currentPage === totalPages}>Next</button>
                        </div>
                        <a href="#" className="add-user-link">Add User</a>
                    </div>

                    {/* Category Management */}
                    <div className="grid-item">
                        <h2>Manage Categories</h2>
                        <input type="text" placeholder="Category Name" value={categoryName} onChange={(e) => setCategoryName(e.target.value)} />
                        <button onClick={addCategory}>Add Category</button>
                        <ul>
                            {categories.map((cat, idx) => (
                                <li key={idx}>
                                    {cat.name} - {cat.enabled ? "Enabled" : "Disabled"} 
                                    <button onClick={() => toggleCategory(idx)}>
                                        {cat.enabled ? "Disable" : "Enable"}
                                    </button>
                                </li>
                            ))}
                        </ul>
                    </div>

                    {/* Question Management */}
                    <div className="grid-item">
                        <h2>Create Questions</h2>
                        <input type="text" placeholder="Question" value={questionText} onChange={(e) => setQuestionText(e.target.value)} />
                        {options.map((opt, idx) => (
                            <div key={idx}>
                                <input type="text" placeholder={`Option ${idx + 1}`} value={opt} onChange={(e) => {
                                    const newOptions = [...options];
                                    newOptions[idx] = e.target.value;
                                    setOptions(newOptions);
                                }} />
                                <input type="number" placeholder="Weight" value={weights[idx]} onChange={(e) => {
                                    const newWeights = [...weights];
                                    newWeights[idx] = parseInt(e.target.value) || 0;
                                    setWeights(newWeights);
                                }} />
                            </div>
                        ))}
                        <button onClick={addQuestion}>Add Question</button>
                        <ul>
                            {questions.map((q, idx) => (
                                <li key={idx}>{q.text}</li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
