import { useState ,useEffect} from "react";

const CategoryList = () => {
    // Sample data

    const [categories, setCategories] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

    useEffect(() => {
        fetchCategories();
    }, [currentPage]);

    const fetchCategories = async () => {
        try {


            const token = localStorage.getItem("token"); // Get the token from local storage or session storage
            if (!token) {
                setError("Unauthorized: No token found");
                setLoading(false);
                return;
            }

            const response =await fetch("http://your-backend-url/api/paginated_categories?page=1&per_page=5", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`  // Add JWT token here
                }
            })


            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const data = await response.json();
            setCategories(data.categories);
            setTotalPages(data.total_pages);
            setCurrentPage(data.current_page);



        } catch (error) {
            console.error("Error fetching categories:", error);
        }
    };

    const nextPage = () => {
        if (currentPage < totalPages) setCurrentPage(currentPage + 1);
    };

    const prevPage = () => {
        if (currentPage > 1) setCurrentPage(currentPage - 1);
    };



    // Pagination logic




    // Edit and Toggle Active Status
    const editCategory = (id) => alert(`Editing category ${id}`);
    const toggleCategoryStatus = (id) => {
        setCategories(categories.map(cat => cat.id === id ? { ...cat, active: !cat.active } : cat));
    };

    return (
        <div className="grid-item">
            <h2>Themes</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Status</th>
                        <th>Edit  Disable</th>
                    </tr>
                </thead>
                <tbody>

                    {categories.map((cat) => (
                        <tr key={cat.id}>
                            <td>{cat.name}</td>

                            <td>{cat.description}</td>
                            <td>{cat.active ? "Active" : "Inactive"}</td>
                            <td>
                                <button className="icon-button" onClick={() => editCategory(cat.id)}>
                                    <img src="/icons/edit.png" alt="Edit" className="icon-img" />
                                </button>
                                <button className="icon-button" onClick={() => toggleCategoryStatus(cat.id)}>
                                    <img src={cat.active ? "/icons/disable.png" : "/icons/no_action.png"} alt="Toggle Status" className="icon-img" />
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="pagination">
                <button className="button-link" onClick={prevPage} disabled={currentPage === 1}>Prev</button>
                <span className="page-info"> Page {currentPage} of {totalPages} </span>
                <button className="button-link" onClick={nextPage} disabled={currentPage === totalPages}>Next</button>
            </div>
            <a href="#" className="add-user-link">Add New Theme</a>
        </div>
    );
};

export default CategoryList;
