import React from "react";

import { BrowserRouter as Router, Route, Routes ,useLocation} from "react-router-dom";

import SideBar from "./components/SideBar";
import Dashboard from "./pages/Dashboard";
import RiskProfiles from "./pages/RiskProfiles";
import Login from "./pages/Login";
import ResultsPage from "./pages/ResultsPage";
import AdminDashboard from "./pages/AdminDashboard";



function App() {

  const location = useLocation();
  const hideSidebarRoutes = ["/","/login"];
  const hideSidebar = location.pathname === "/"; // Hide sidebar only on login page

  return (
    <div className="dashboard-container">
      {/* Sidebar should only render if not on login page */}
      {!hideSidebar && <SideBar />} 

      {/* Main content area where pages render */}
      <div className={`main-content ${hideSidebar ? "full-width" : ""}`}>

        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/adminDashboard" element={<AdminDashboard />} />

        </Routes>
      </div>
    </div>
  );
}

export default App;
