import React, { useState, useEffect } from "react";
import Modal from "react-modal";

Modal.setAppElement("#root");

const QuestionsTable = () => {
  const current_user_type = localStorage.getItem("user_type");
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [questions, setQuestions] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [questionText, setQuestionText] = useState("");
  const [editingQuestion, setEditingQuestion] = useState(null);
  const [answerText, setAnswerText] = useState("");
  const [flag_select, setFlag] = useState(true);




  useEffect(() => {
    fetchCategories();
  }, []);

  useEffect(() => {
    if (selectedCategory) fetchQuestions(selectedCategory);
  }, [selectedCategory]);

  const fetchCategories = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/api/categories");
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const fetchQuestions = async (categoryId) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/api/questions?category_id=${categoryId}&limit=0`);
      const resp_data = await response.json();

      setQuestions(resp_data["data"]);
    } catch (error) {
      console.error("Error fetching questions:", error);
    }
  };

  const handleAddOrEditQuestion = async (e) => {
    e.preventDefault();
    const url = editingQuestion
      ? `http://127.0.0.1:5000/api/questions/${editingQuestion.id}`
      : `http://127.0.0.1:5000/api/answers/${editingQuestion.id}`;
    const method =  "POST";

    try {
      const response = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({text: questionText, category_id: selectedCategory }),
      });
      if (response.ok) {
        fetchQuestions(selectedCategory);
        closeModal();
      }
    } catch (error) {
      console.error("Error saving question:", error);
    }
  };

  const handleAddOrEditAnswer = async (e) => {
    e.preventDefault();
    const url = `http://127.0.0.1:5000/api/edit_answers/${editingQuestion.id}`;
    const method =  "POST";

    try {
      const response = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({text: answerText, category_id: selectedCategory,  }),
      });
      if (response.ok) {
        fetchQuestions(selectedCategory);
        closeModal();
      }
    } catch (error) {
      console.error("Error saving question:", error);
    }
  };

  const handleEdit = (question) => {


    setFlag(true);
    setEditingQuestion(question);
    setQuestionText(question.question_text);
    setIsModalOpen(true);
  };

  const handleEditAnswer= (question,answer) => {
    setFlag(false);
    setEditingQuestion(question);
    setAnswerText(answer.text);
    setAnswerScore(answer.score);
    setIsModalOpen(true);
  };

  const handleDisable = async (questionId) => {
    try {
      await fetch(`http://127.0.0.1:5000/api/questions/${questionId}`, { method: "DELETE" });
      fetchQuestions(selectedCategory);
    } catch (error) {
      console.error("Error disabling question:", error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setQuestionText("");
    setEditingQuestion(null);
    setFlag(true);
  };

  return (
    <div>
      <h1>Manage Questions</h1>
      <label>Category:</label>
      <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
        <option value="">Select Category</option>
        {categories.map((cat) => (
          <option key={cat.id} value={cat.id}>{cat.name}</option>
        ))}
      </select>

      <table className="questions-table">
            <thead>
                <tr>
                    <th>Question</th>
                    <th></th>
                    <th></th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {questions.map((question) => (
                    <React.Fragment key={question.id}>
                        <tr>
                            <td><strong>{question.question_text}</strong></td>
                            <td> </td>
                            <td> </td>
                            <td>
                                <button onClick={() => handleEdit(question)} disabled={ question.status==="inactive" || current_user_type === "MEMBER"}>Edit</button>
                                <button onClick={() => handleDisable(question.id)} disabled={current_user_type === "MEMBER"}>Disable</button>
                            </td>
                        </tr>
                        {question.options.map((answer) => (
                            <tr key={`${question.id}:${answer.score}`} className="answer-row">
                                <td>{answer.text}</td>
                                <td> {answer.score}</td>
                                <td>


                                    <button
                                        className="icon-button"
                                        onClick={() => handleEditAnswer(question,answer)}
                                        disabled={ question.status==="inactive" || current_user_type === "MEMBER"}
                                        style={{ opacity: question.status === "inactive" ? 0.5 : 1, cursor: question.status === "inactive" ? "not-allowed" : "pointer" }}
                                      >
                                        <img src="/icons/edit.png" alt="Edit" className="icon-img" />
                                      </button>
                                </td>
                            </tr>
                        ))}
                    </React.Fragment>
                ))}
            </tbody>
        </table>


      <Modal isOpen={isModalOpen} onRequestClose={closeModal} className="modal-content" overlayClassName="modal-overlay">

        {flag_select && (
          <div>
           <h2>"Edit Question" </h2>
             <form onSubmit={handleAddOrEditQuestion}>
                <label>Question:</label>

                <input type="text" value={questionText} onChange={(e) => setQuestionText(e.target.value)} required />
                <div>
                  <button type="button" onClick={closeModal}>Cancel</button>
                  <button type="submit">Save</button>
                </div>
              </form>
            </div>
        )}
        {!flag_select && (
          <div>
           <h2>"Edit Answer" </h2>
             <form onSubmit={handleAddOrEditAnswer}>
                <label>Answer:</label>
                <input type="text" value={answerText} onChange={(e) => setAnswerText(e.target.value)} required />
                <div>
                  <button type="button" onClick={closeModal}>Cancel</button>
                  <button type="submit">Save</button>
                </div>
              </form>
            </div>
        )}

      </Modal>
    </div>
  );
};

export default QuestionsTable;
