import React, { useState, useEffect } from "react";
import Modal from "react-modal";

Modal.setAppElement("#root");

const EditUserModal = ({ user, closeModal, refreshUsers }) => {
    const [formData, setFormData] = useState({
        firstname: "",
        lastname: "",
        email: "",
        status: "",
        user_type: "",
        department_id: ""
    });

    const [departments, setDepartments] = useState([]);

    // Fetch departments from API
    useEffect(() => {
        const fetchDepartments = async () => {
            try {
                const response = await fetch("http://127.0.0.1:5000/api/departments"); // API endpoint to get departments
                const data = await response.json();
                alert(data.departments)
                setDepartments(data.departments);
                 // Assuming response has a key 'departments'
            } catch (error) {
                console.error("Error fetching departments:", error);
            }
        };

        fetchDepartments();
    }, []);

    // Populate form when user data is received
    useEffect(() => {
        if (user) {
            setFormData({
                firstname: user.firstname || "",
                lastname: user.lastname || "",
                email: user.email || "",
                status: user.status || "",
                user_type: user.user_type || "",
                department_id: user.department_id || ""
            });
        }
    }, [user]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const token = localStorage.getItem("token");
            const response = await fetch(`http://127.0.0.1:5000/api/update_user/${user.id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify(formData)
            });

            const data = await response.json();
            if (data.status === "SUCCESS") {
                alert("User updated successfully!");
                refreshUsers();  // Refresh user list
                closeModal();    // Close modal
            } else {
                alert(data.message);
            }
        } catch (error) {
            console.error("Error updating user:", error);
            alert("An error occurred. Please try again.");
        }
    };

    return (
        <Modal isOpen={true} onRequestClose={closeModal} className="modal-content" overlayClassName="modal-overlay">
            <h2>Edit User</h2>
            <form onSubmit={handleSubmit} autoComplete="off">
                <label>First Name:</label>
                <input type="text" name="firstname" value={formData.firstname} onChange={handleChange} required />

                <label>Last Name:</label>
                <input type="text" name="lastname" value={formData.lastname} onChange={handleChange} required />

                <label>Email:</label>
                <input type="email" name="email" value={formData.email} disabled />

                <label>Status:</label>
                <select name="status" value={formData.status} onChange={handleChange} required>
                    <option value="ACTIVE">ACTIVE</option>
                    <option value="INACTIVE">INACTIVE</option>
                </select>

                <label>User Type:</label>
                <select name="user_type" value={formData.user_type} onChange={handleChange} required>
                    <option value="ADMIN">ADMIN</option>
                    <option value="MEMBER">MEMBER</option>
                </select>

                <label>Department:</label>
                <select name="department_id" value={formData.department_id} onChange={handleChange} required>
                    <option value="">Select Department</option>
                    {departments.map(dep => (
                        <option key={dep.id} value={dep.id}>
                            {dep.name}
                        </option>
                    ))}
                </select>

                <div className="modal-actions">
                    <button type="button" className="btn-cancel" onClick={closeModal}>Cancel</button>
                    <button type="submit" className="btn-save">Save</button>
                </div>
            </form>
        </Modal>
    );
};

export default EditUserModal;
