import React, { useState, useEffect } from "react";
import AddCategoryModal from "./AddCategoryModal";
import EditCategoryModal from "./EditCategoryModal";

const CategoryTable = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/api/categories");
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const handleAddCategory = (newCategory) => {
    setCategories([...categories, newCategory]);
    setIsAddModalOpen(false);
  };

  const handleEditCategory = (updatedCategory) => {
    setCategories(categories.map(cat => cat.id === updatedCategory.id ? updatedCategory : cat));
    setIsEditModalOpen(false);
  };

  const handleDeleteCategory = async (id) => {
    try {
      await fetch(`http://127.0.0.1:5000/api/categories/${id}`, { method: "DELETE" });
      setCategories(categories.filter(cat => cat.id !== id));
    } catch (error) {
      console.error("Error deleting category:", error);
    }
  };

  return (
    <div>
      <h2>Category Management</h2>
      <button onClick={() => setIsAddModalOpen(true)}>Add Category</button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Description </th>
            <th>Status </th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map(category => (
            <tr key={category.id}>
              <td>{category.id}</td>
              <td>{category.name}</td>
              <td>{category.description}</td>
              <td>{category.status}</td>
              <td>
                <button onClick={() => { setSelectedCategory(category); setIsEditModalOpen(true); }}>Edit</button>
                <button onClick={() => handleDeleteCategory(category.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isAddModalOpen && <AddCategoryModal closeModal={() => setIsAddModalOpen(false)} addCategory={handleAddCategory} />}
      {isEditModalOpen && <EditCategoryModal category={selectedCategory} closeModal={() => setIsEditModalOpen(false)} editCategory={handleEditCategory} />}
    </div>
  );
};

export default CategoryTable;
