import React, { useState } from "react";

const AddUser = ({ closeModal, refreshUsers }) => {
    const [formData, setFormData] = useState({
        firstname: "",
        lastname: "",
        email: "",
        user_type: "MEMBER"
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");
            const response = await fetch("http://127.0.0.1:5000/api/add_user", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify(formData)
            });

            const data = await response.json();
            if (data.status === "SUCCESS") {
                alert("User added successfully!");
                refreshUsers();  // Refresh user list
                closeModal();    // Close modal
            } else {
                alert(data.message);
            }
        } catch (error) {
            console.error("Error adding user:", error);
            alert("An error occurred. Please try again.");
        }
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Add New User</h2>
                <form onSubmit={handleSubmit}>
                    <label>
                        First Name:
                        <input type="text" name="firstname" value={formData.firstname} onChange={handleChange} required />
                    </label>
                    <label>
                        Last Name:
                        <input type="text" name="lastname" value={formData.lastname} onChange={handleChange} required />
                    </label>
                    <label>
                        Email:
                        <input type="email" name="email" value={formData.email} onChange={handleChange} required />
                    </label>
                    <label>
                        User Type:
                        <select name="user_type" value={formData.user_type} onChange={handleChange} required>
                            <option value="ADMIN">ADMIN</option>
                            <option value="MEMBER">MEMBER</option>
                        </select>
                    </label>

                    <div className="modal-actions">
                        <button type="button" className="btn-cancel" onClick={closeModal}>Cancel</button>
                        <button type="submit" className="btn-save">Save</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default AddUser;
