import React, { useState,useEffect,refreshUsers } from "react";
import EditUserModal from "./modals/EditUserModal";
import AddUser from "./modals/AddUser";

const UserTable = ({ users, refreshUsers }) => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);


  const openEditModal = (user) => {

    setSelectedUser(user); // Store selected user in state
  };

  const closeEditModal = () => {
    setSelectedUser(null); // Close modal
  };

  const fetchUsers = async () => {
    try {
        const token = localStorage.getItem("token");
        if (!token) {
            console.log("TOKEN failed");
            return;
        }

        const response = await fetch("http://127.0.0.1:5000/api/get_users", {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`
            }
        });

        if (response.status === 401) {
            alert("Session timed out. Please log in again.");
            localStorage.removeItem("token");
            window.location.href = "/";
            return;
        }

        if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        refreshUsers();  // ✅ Update users state
    } catch (error) {
        console.error("Error fetching users:", error);
    }
};

// Fetch users when the component loads
useEffect(() => {
    fetchUsers();
}, []);

  return (


    <div>
      <div className="link-container">
        <button className="btn-add-user" onClick={() => setIsModalOpen(true)}>Add User</button>
        {isModalOpen && <AddUser closeModal={() => setIsModalOpen(false)} refreshUsers={fetchUsers} />}
      </div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Status</th>
            <th>User Type</th>
            <th className="align_right">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (

            <tr key={user.id}>
              <td>{user.firstname} {user.lastname}</td>
              <td>{user.email}</td>
              <td>{user.status}</td>
              <td>{user.user_type}</td>
              <td className="align_right">
                <button className="icon-button" onClick={() => openEditModal(user)}>
                  <img src="/icons/edit.png" alt="Edit" className="icon-img" />
                </button>
              </td>
            </tr>

          ))
          }
        </tbody>
      </table>

      {/* Render Edit Modal When a User is Selected */}
      {selectedUser && (
        <EditUserModal
          user={selectedUser}
          closeModal={closeEditModal}
          updateUser={updateUser}
        />
      )}
    </div>
  );
};

export default UserTable;
